      />

      <motion.img
        src={cloudImage}
        alt="cloud"
        className="hidden md:block absolute right-[8%] top-[35%] w-[250px] opacity-70"
        initial={{ x: 100, opacity: 0, y: 0 }}
        animate={{ 
          x: 0, 
          opacity: 0.7,
          y: [0, -12, 0]
        }}
        transition={{ 
          x: { duration: 1.4, ease: "easeOut" },
          opacity: { duration: 1.4, delay: 0.2, ease: "easeOut" },
