  --card-foreground: hsl(250, 60%, 25%);
  --popover: hsl(220, 100%, 99%);
  --popover-foreground: hsl(250, 60%, 25%);
  --primary: hsl(260, 85%, 60%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(220, 80%, 92%);
  --secondary-foreground: hsl(250, 60%, 25%);
  --muted: hsl(220, 60%, 88%);
  --muted-foreground: hsl(250, 35%, 50%);
  --accent: hsl(210, 100%, 55%);
  --accent-foreground: hsl(0, 0%, 100%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --border: hsl(220, 50%, 85%);
  --input: hsl(220, 80%, 94%);
  --ring: hsl(260, 85%, 60%);
  --radius: 0.5rem;
  --font-sans: 'Inter', sans-serif;
  --font-retro: 'VT323', monospace;
  --font-pixel: 'Press Start 2P', cursive;
}

@layer base {
  * {
    @apply border-border;
  }

  html {
    scroll-behavior: smooth;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    overflow-x: hidden;
    transition: background-color 0.3s ease, color 0.3s ease;
    -webkit-overflow-scrolling: touch;
  }

  /* Performance optimizations for mobile */
  @media (max-width: 768px) {
    * {
      -webkit-transform: translateZ(0);
      transform: translateZ(0);
      -webkit-backface-visibility: hidden;
      backface-visibility: hidden;
    }
  }

  [data-theme="night"] body {
    background: linear-gradient(180deg, #1a1d3d 0%, #0a0b1a 100%);
    background-attachment: fixed;
    color: hsl(180, 100%, 90%);
    position: relative;
  }

  [data-theme="night"] body::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: 
      linear-gradient(rgba(255, 255, 255, 0.03) 1px, transparent 1px),
      linear-gradient(90deg, rgba(255, 255, 255, 0.03) 1px, transparent 1px);
    background-size: 50px 50px;
    pointer-events: none;
    z-index: 0;
  }

  [data-theme="day"] body {
    background: linear-gradient(180deg, #5865f2 0%, #4752c4 100%);
    background-attachment: fixed;
    color: hsl(0, 0%, 100%);
    position: relative;
  }

  [data-theme="day"] body::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: 
      linear-gradient(rgba(255, 255, 255, 0.1) 1px, transparent 1px),
      linear-gradient(90deg, rgba(255, 255, 255, 0.1) 1px, transparent 1px);
    background-size: 50px 50px;
    pointer-events: none;
    z-index: 0;
  }
}

@layer utilities {
  .retro-text {
    font-family: var(--font-retro);
    text-shadow: 0 0 10px currentColor;
  }

  .pixel-text {
    font-family: var(--font-pixel);
    image-rendering: pixelated;
  }

  .crt-effect {
    position: relative;
  }

  .crt-effect::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
      transparent 50%, 
      rgba(0, 0, 0, 0.1) 50%
    );
    background-size: 100% 4px;
    pointer-events: none;
    z-index: 1;
  }

  [data-theme="night"] .grain-texture {
    background-image: 
      radial-gradient(circle at 1px 1px, rgba(180, 255, 255, 0.1) 1px, transparent 0);
    background-size: 20px 20px;
    pointer-events: none;
  }

  [data-theme="day"] .grain-texture {
    background-image: 
      radial-gradient(circle at 1px 1px, rgba(100, 50, 150, 0.08) 1px, transparent 0);
    background-size: 20px 20px;
    pointer-events: none;
  }

  .retro-glow {
    box-shadow: 0 0 20px currentColor, inset 0 0 20px rgba(255, 255, 255, 0.1);
  }

  .bubble-glow {
    box-shadow: 
      0 0 30px rgba(139, 92, 246, 0.3),
      0 0 60px rgba(59, 130, 246, 0.2),
      inset 0 0 30px rgba(255, 255, 255, 0.1);
  }

  .bubble {
    border-radius: 9999px;
  }

  .musical-wave {
    background: repeating-linear-gradient(
      90deg,
      transparent,
      transparent 10px,
      currentColor 10px,
      currentColor 11px
    );
    opacity: 0.1;
  }

  .vinyl-spin {
    animation: spin 3s linear infinite;
  }

  .note-float {
    animation: noteFloat 4s ease-in-out infinite;
  }

  @keyframes spin {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }

  @keyframes noteFloat {
    0%, 100% { 
      transform: translateY(0px) translateX(0px) rotate(0deg);
      opacity: 0.6;
    }
    25% {
      transform: translateY(-20px) translateX(5px) rotate(5deg);
      opacity: 1;
    }
    75% {
      transform: translateY(-10px) translateX(-5px) rotate(-5deg);
      opacity: 0.8;
    }
  }

  .hexagon-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1rem;
  }

  .smooth-scroll {
    scroll-behavior: smooth;
  }

  @keyframes scanline {
    0% { transform: translateY(-100%); }
    100% { transform: translateY(100vh); }
  }

  .scanline {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    height: 2px;
    background: linear-gradient(transparent, rgba(255, 255, 255, 0.3), transparent);
    animation: scanline 8s linear infinite;
    pointer-events: none;
    z-index: 999;
  }
}

