        <circle cx="50" cy="50" r="35" fill={`url(#planetGradient${delay})`}/>
        <ellipse cx="50" cy="50" rx="50" ry="8" fill="none" stroke={color} strokeWidth="2" opacity="0.6"/>
      </svg>
    </motion.div>
  );
}

export function RetroGrid({ className = "" }: { className?: string }) {
